    //       imageName: fileName,
    //       imageType: fileType,
    //       action: action
    //     });
    //   };
    // }
  };

  // Upload file to Azure
  uploadFiles = event => {
    console.log(event.target.files[0]);
    let fileName = event.target.files[0].name;
    S3FileUpload.uploadFile(event.target.files[0], config)
      .then(data => {
        let sendData = {
          userProfileId: this.state.userProfileId,
          userId: this.state.userId,
          resumeURL: data.location,
          resumeName: fileName
        };
        console.log(data);
        theRapidHireApiService('addResume', sendData)
          .then(response => {
            if (response.data.status === 'Success') {
              this.getUserProfileData(this.state.userId);
            }
          })
          .catch(err => {
            console.log(err);
          });
      })
      .catch(err => console.error(err));
  };

  DeleteFile = event => {
    S3FileUpload.deleteFile(this.state.resumeName, config)
      .then(response => {
        console.log(response);
        let sendData = {
          userProfileId: this.state.userProfileId,
          userId: this.state.userId,
          resumeURL: null,
          resumeName: null
        };

        theRapidHireApiService('addResume', sendData)
          .then(response => {
            if (response.data.status === 'Success') {
              this.getUserProfileData(this.state.userId);
            }
          })
          .catch(err => {
            console.log(err);
          });
      })
      .catch(err => console.error(err));
  };

